  yi = __riscv_vand_vx_u64m1 (ai, 0x7ff0000000000000L, vl);
  m1 = __riscv_vmseq_vx_u64m1_b64 (yi, 0x7ff0000000000000L, vl);
  m2 = __riscv_vmseq_vx_u64m1_b64 (yi, 0L, vl);
