  jbm_sinnxf32_coswc (__riscv_vfsub_vf_f32m1 (y, 3.f * M_PI_2f, vl), &c2, &s2,
                      vl);
  m = __riscv_vmflt_vf_f32m1_b32 (y, 7.f * M_PI_4f, vl);
  s1 = __riscv_vmerge_vvm_f32m1 (s1, jbm_nxf32_opposite (s2, vl), m, vl);
