  s1 = __riscv_vmerge_vvm_f32m1 (s1, s2, m, vl);
  c1 = __riscv_vmerge_vvm_f32m1 (c1, c2, m, vl);
  jbm_sinnxf32_coswc (y, &s2, &c2, vl);
  m = __riscv_vmflt_vf_f32m1_b32 (y, M_PI_4f, vl);
