    case JBM_FLUX_LIMITER_TYPE_SUPERBEE:
      return jbm_nxf32_flux_limiter_superbee (d1, d2, vl);
    case JBM_FLUX_LIMITER_TYPE_MINMOD:
      return jbm_nxf32_flux_limiter_minmod (d1, d2, vl);
    case JBM_FLUX_LIMITER_TYPE_VAN_LEER:
      return jbm_nxf32_flux_limiter_VanLeer (d1, d2, vl);
    case JBM_FLUX_LIMITER_TYPE_VAN_ALBADA:
      return jbm_nxf32_flux_limiter_VanAlbada (d1, d2, vl);
    case JBM_FLUX_LIMITER_TYPE_MINSUPER:
